const setMonthName = (monthNumber) => {
    let result = {}
    switch (monthNumber) {
        case 1:
            result = {
                shortNameEng: "Jan",
                shortNameHeb: "",
            }
            break;
        case 2:
            result = {
                shortNameEng: "Feb",
