                shortNameHeb: "",
            }
            break;
        case 6:
            result = {
                shortNameEng: "Jun",
                shortNameHeb: "",
            }
            break;
        case 7:
            result = {
                shortNameEng: "Jul",
                shortNameHeb: "",
            }
            break;
        case 8:
            result = {
                shortNameEng: "Aug",
                shortNameHeb: "",
            }
            break;
        case 9:
            result = {
                shortNameEng: "Sep",
                shortNameHeb: "",
            }
            break;
        case 10:
            result = {
                shortNameEng: "Oct",
                shortNameHeb: "",
            }
            break;
        case 11:
            result = {
                shortNameEng: "Nov",
                shortNameHeb: "",
            }
            break;
        case 12:
            result = {
